const InfiniteScroll = () => {
  const scrollerRef = useRef<HTMLUListElement>(null);

  useEffect(() => {
    const scroller = scrollerRef.current;
    if (!scroller) return;

    const duplicatedContent = scroller.innerHTML;
    scroller.innerHTML += duplicatedContent;

    let animation = scroller.animate(
      [{ transform: "translateX(0)" }, { transform: "translateX(-50%)" }],
      {
        duration: 20000, 
        iterations: Infinity,
        easing: "linear",
      }
    );

    return () => animation.cancel();
  }, []);

  return (
    <div className="w-[500px] overflow-hidden flex justify-center items-center py-5">
      <ul ref={scrollerRef} className="flex gap-6 whitespace-nowrap">
        {tags.map((tag, index) => (
          <li key={index} className="flex flex-col items-center px-4 py-2 rounded-md shadow-lg">
            <img src={images[index]} alt={tag} className="w-14 h-14 mb-2 object-fit"/>
            <p className="font-bold">{tag}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export const TechStackSlider: React.FC = () => {
  return (
    <div className="flex flex-col items-center py-5">
      <h1 className="text-2xl font-bold">Tech Stack</h1>
      <InfiniteScroll />
    </div>
  );
};
